package main

import (
	"fmt"
)

// https://leetcode.com/problems/longest-valid-parentheses/

// Given a string containing just the characters '(' and ')',
// find the length of the longest valid (well-formed) parentheses substring.
// Example 1:
//   Input: "(()"
//   Output: 2
//   Explanation: The longest valid parentheses substring is "()"
// Example 2:
//   Input: ")()())"
//   Output: 4
//   Explanation: The longest valid parentheses substring is "()()"

// less than O(n2)
func longestValidParentheses1(s string) int {
	re := 0
	startat := make([]int, len(s))
	hasnew := false
	for i := 0; i < len(s)-1; i++ {
		if s[i] == '(' && s[i+1] == ')' {
			startat[i] = 2
			re = 2
			hasnew = true
		}
	}
	for hasnew {
		hasnew = false
		i := 0
		for i < len(s) {
			if startat[i] > 0 {
				if i-1 >= 0 && i+startat[i] < len(s) && s[i-1] == '(' && s[i+startat[i]] == ')' {
					startat[i-1] = startat[i] + 2
					re = max(re, startat[i-1])
					hasnew = true
					i = i - 1 + startat[i-1]
				} else if i+startat[i] < len(s) && startat[i+startat[i]] > 0 {
					startat[i] += startat[i+startat[i]]
					re = max(re, startat[i])
					i = i + startat[i]
					hasnew = true
				} else {
					i += startat[i]
				}
			} else {
				i++
			}
		}
	}
	return re
}

// O(n)
func longestValidParentheses2(s string) int {
	re := 0
	startat := make([]int, len(s))
	endat := make([]int, len(s))
	i := 0
	for i < len(s) {
		for {
			if startat[i] == 0 && i+1 < len(s) && s[i] == '(' && s[i+1] == ')' {
				startat[i] = 2
				endat[i+1] = 2
			} else if startat[i] > 0 && i-1 >= 0 && i+startat[i] < len(s) && s[i-1] == '(' && s[i+startat[i]] == ')' {
				startat[i-1] = startat[i] + 2
				endat[i+startat[i]] = startat[i] + 2
				i = i - 1
			} else if startat[i] > 0 && i-1 >= 0 && endat[i-1] > 0 {
				startat[i-endat[i-1]] = startat[i] + endat[i-1]
				endat[i+startat[i]-1] = startat[i] + endat[i-1]
				i = i - endat[i-1]
			} else {
				break
			}
		}
		if startat[i] > 0 {
			re = max(re, startat[i])
			i += startat[i]
		} else {
			i++
		}
	}
	return re
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

func main() {
	fmt.Println(longestValidParentheses1("(()"))
	fmt.Println(longestValidParentheses1(")()())"))
	fmt.Println(longestValidParentheses1("()(()"))
	fmt.Println(longestValidParentheses1("(()(()))"))
	fmt.Println(longestValidParentheses1(")()(()(()))()))("))
	fmt.Println(longestValidParentheses1(long))
	fmt.Println(longestValidParentheses2("(()"))
	fmt.Println(longestValidParentheses2(")()())"))
	fmt.Println(longestValidParentheses2("()(()"))
	fmt.Println(longestValidParentheses2(")()(()(()))()))("))
	fmt.Println(longestValidParentheses2(long))
}

