package main

import (
	"fmt"
)

// https://leetcode.com/problems/longest-valid-parentheses/

// Given a string containing just the characters '(' and ')',
// find the length of the longest valid (well-formed) parentheses substring.
// Example 1:
//   Input: "(()"
//   Output: 2
//   Explanation: The longest valid parentheses substring is "()"
// Example 2:
//   Input: ")()())"
//   Output: 4
//   Explanation: The longest valid parentheses substring is "()()"

// less than O(n2)
func longestValidParentheses1(s string) int {
	re := 0
	startat := make([]int, len(s))
	hasnew := false
	for i := 0; i < len(s)-1; i++ {
		if s[i] == '(' && s[i+1] == ')' {
			startat[i] = 2
			re = 2
			hasnew = true
		}
	}
	for hasnew {
		hasnew = false
		i := 0
		for i < len(s) {
			if startat[i] > 0 {
				if i-1 >= 0 && i+startat[i] < len(s) && s[i-1] == '(' && s[i+startat[i]] == ')' {
					startat[i-1] = startat[i] + 2
					re = max(re, startat[i-1])
					hasnew = true
					i = i - 1 + startat[i-1]
				} else if i+startat[i] < len(s) && startat[i+startat[i]] > 0 {
					startat[i] += startat[i+startat[i]]
					re = max(re, startat[i])
					i = i + startat[i]
					hasnew = true
				} else {
					i += startat[i]
				}
			} else {
				i++
			}
		}
	}
	return re
}

// O(n) time, O(n) space
func longestValidParentheses2(s string) int {
	re := 0
	startat := make([]int, len(s))
	endat := make([]int, len(s))
	i := 0
	for i < len(s) {
		for {
			if startat[i] == 0 && i+1 < len(s) && s[i] == '(' && s[i+1] == ')' {
				startat[i] = 2
				endat[i+1] = 2
			} else if startat[i] > 0 && i-1 >= 0 && i+startat[i] < len(s) && s[i-1] == '(' && s[i+startat[i]] == ')' {
				startat[i-1] = startat[i] + 2
				endat[i+startat[i]] = startat[i] + 2
				i = i - 1
			} else if startat[i] > 0 && i-1 >= 0 && endat[i-1] > 0 {
				startat[i-endat[i-1]] = startat[i] + endat[i-1]
				endat[i+startat[i]-1] = startat[i] + endat[i-1]
				i = i - endat[i-1]
			} else {
				break
			}
		}
		if startat[i] > 0 {
			re = max(re, startat[i])
			i += startat[i]
		} else {
			i++
		}
	}
	return re
}

// O(n) time, O(n) space
func longestValidParenthesesStack(s string) int {
	res := 0
	stack := []int{-1}
	for i, v := range s {
		if v=='(' {
			stack = append(stack, i)
			continue
		}
		// got ')'
		stack = stack[:len(stack)-1]		
		if len(stack)==0 {  // empty stack
			stack = append(stack, i)
		} else {
			res = max(res, i - stack[len(stack)-1])
		}
	}
	return res
}

// two turns, from left to right, and from right to left
func longestValidParenthesesO1Space(s string) int {
	left, right := 0, 0
	res := 0
	for i:=0; i<len(s); i++ {
		if s[i]=='(' {
			left++
		} else {
			right++
		}
		if left==right {
			res = max(res, 2*left)
		}
		if right>left {  // from left to right, if right>left, it's inlvaid
			left, right = 0, 0
		}
	}
	left, right = 0, 0
	for i:=len(s)-1; i>=0; i-- {
		if s[i]=='(' {
			left++
		} else {
			right++
		}
		if left==right {
			res = max(res, 2*left)
		}
		if left>right {  // from right to left, if left>right, it's inlvaid
			left, right = 0, 0
		}
	}
	return res
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

func main() {
	strs := []string{"(()", ")()())", "()(()", "(()(()))", ")()(()(()))()))(", "()(()", "(()(((()", ")()())", long}
	funcs := []func(string)int{
		longestValidParentheses1, 
		longestValidParentheses2, 
		longestValidParenthesesStack,
		longestValidParenthesesO1Space,
	}
	for _, s := range strs {
		for _, v := range funcs {
			fmt.Print(v(s), " ")
		}
		fmt.Println()
	}
}

